    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        try {
            cod  =   jComboBox1.getSelectedItem().toString();
            dest =   jDest.getText();
            subj =   jSub.getText();
            mnsj =   jMensaje.getText();

            System.out.println("Codificacion: " + cod + "\nDestinatario:" + dest +
                               "\nAsunto: " + subj + "\nMensaje: " + mnsj);

            new EmailEnvio(protocolo, puerto, host, cuenta, pass, subj, cod, mnsj, dest);
        } catch (Exception e) {
            e.printStackTrace();
        }
    }//GEN-LAST:event_jButton1ActionPerformed

    private void bAceptarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bAceptarActionPerformed
       
        try {
            protocolo   =   jProtocolo.getText();
            host        =   jHost.getText();
            puerto      =   jPuerto.getText();
            cuenta      =   jCuenta.getText();
            pass        =   jPass.getText();
            jTabbedPane1.setSelectedIndex(1);
            System.out.println("Protocolo: " + protocolo + "\nHost:" + host +
                               "\nPuerto: " + puerto + "\nCuenta: " + cuenta +
                               "\nPassword: " + pass);
        } catch (Exception e) {
        }
    }//GEN-LAST:event_bAceptarActionPerformed

